import React from 'react';
import {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';

const KEY = "stocks";

const App = () => {

  const [stocks, setStocks] = useState([]);

  return (
    <div className="App">
      <header className="App-header">
        <StocksList stocks={stocks} setStocks={setStocks}></StocksList>
      </header>
    </div>
  );
}

const StocksList = ({stocks, setStocks}) => {

  const getStocks = () => {
    if (!storageIsEmpty()) {
      setStocks(getStockList().map(x => x));
    }
  }

  useEffect(() => {
    // side effect
    getStocks();

    // cleanup function -- called when dependencies change or component is unmounted
    return () => {
      // setStocklist([]);
    };
  }, [])

  return (
    <div>
        {stocks}
    </div>
  )

}

// helper functions
function storageIsEmpty() {
  return localStorage.getItem(KEY) === null;
}

function getStockList() {
  return JSON.parse(localStorage.getItem(KEY));
}

export default App;
